<?phpfunction ds_flickr() {	register_widget( 'ds_flickr' );}add_action( 'widgets_init', 'ds_flickr' );class ds_flickr extends WP_Widget{    function ds_flickr()     {		$widget_ops = array(             'classname' => 'ds_flickr',             'description' => __( 'Widget with a simple ds_flickr information.', 'dubstrap' )        );		$control_ops = array( 'id_base' => 'ds_flickr' );		$this->WP_Widget( 'ds_flickr', __( 'DS Flickr', 'dubstrap' ), $widget_ops, $control_ops );   	}		function form( $instance )	{		global $icons_name;				$defaults = array(             'title' => __( 'Recent Photos', 'dubstrap' ),            'userid' => __( '40450978@N08', 'dubstrap' ),        );        		$instance = wp_parse_args( (array) $instance, $defaults ); ?>				<p>			<label>				<strong><?php _e( 'Title', 'dubstrap' ) ?>:</strong><br />				<input class="widefat" type="text" id="<?php echo $this->get_field_id( 'title' ); ?>" name="<?php echo $this->get_field_name( 'title' ); ?>" value="<?php echo $instance['title']; ?>" />			</label>			<label>				<strong><?php _e( 'UserID', 'dubstrap' ) ?>:</strong><br />				<input class="widefat" type="text" id="<?php echo $this->get_field_id( 'userid' ); ?>" name="<?php echo $this->get_field_name( 'userid' ); ?>" value="<?php echo $instance['userid']; ?>" />			</label>        </p>       		<?php	}		function widget( $args, $instance )	{		extract( $args );		$title = $instance['title'];		$userid = $instance['userid'];				echo $before_widget;                				?>        <div class="widget-flickr">			<h4><?php echo $title ?></h4>							<div id="flickr_badge_wrapper" class="clearfix">				<script src="http://www.flickr.com/badge_code_v2.gne?count=8&amp;display=Random&amp;size=s&amp;layout=x&amp;source=user&amp;user=<?php echo $userid ?>"></script>			</div>		</div>						<?php		echo $after_widget;	}                         function update( $new_instance, $old_instance )     {		$instance = $old_instance;		$instance['title'] = strip_tags( $new_instance['title'] );;		$instance['userid'] = strip_tags( $new_instance['userid'] );;		return $instance;	}	}     ?>